import './BookDetails.css';
import Header from "../Header/Header";
import Footer from '../Footer/Footer';
import { useNavigate } from "react-router-dom";
import cookies from 'js-cookie';


const BookDetail = () => {
//   const navigate = useNavigate();
    const { id } = useParams();
    const [bookDetails, setBookDetails] = useState(null);

    return(
        <div className="backgrounddiv">
            <Header/>
            <div className="card-div-background">
                <div className="cardDiv">
                    <img className="cardImage" src="https://assets.ccbp.in/frontend/react-js/eyes-to-the-wind-book.png" alt="No_image"/>
                    <div className="books-detailsDiv">
                        <h6 className="font-forall title">Title</h6>
                        <p className="font-forall author-descrition">author_name</p>
                        <div className="ratingDiv">
                            <p className="font-forall">Avg Rating: </p>
                            <p className="font-forall">rating</p>
                        </div>
                        <p className="font-forall">Status:<a className="font-forall" href="#0">read_status</a></p>
                    </div>
                </div>
                <hr className="underline"></hr>
                <div className="authorDetails">
                    <h6 className="font-forall title">About Author</h6>
                    <p className="font-forall">pk</p>
                    <h6 className="font-forall title">About Book</h6>
                    <p className="font-forall">cbidqwid</p>
                </div>
            </div>
            <Footer/>
        </div>
    )
        
}

export default BookDetail;
