// import { useEffect } from "react";
import { useNavigate } from "react-router-dom";
// import cookies from 'js-cookie';

const BookCard=(props)=>{
    const navigate = useNavigate();
    const{cardData}=props;

    // useEffect(()=>{
    //     fetchBookDetails();
    // })

    // const fetchBookDetails=async()=>{
    //     const jwttoken=cookies.get('jwt_token');
    //     const bookDetailsApi=await fetch("https://apis.ccbp.in/book-hub/books/"+{books.id},{
    //         headers: {
    //             Authorization: `Bearer ${jwttoken}`,
    //           },
    //     });

    //     const bookDetailsJson=await bookDetailsApi.json();
    //     console.log(bookDetailsJson);
    // }    

    const handleclick=()=>{
        navigate('/BookDetail');
    }

      return(
          <div onClick={handleclick} className="cards-main-container">
              <div className="card-div">
                  <img className="card-image" src={cardData.cover_pic} alt="No_image"/>
                  <div className="books-details-div">
                      <h6 className="font-for-all heading">{cardData.title}</h6>
                      <p className="font-for-all descrition">{cardData.author_name}</p>
                      <div className="rating-div">
                          <p className="font-for-all">Avg Rating: </p>
                          <p className="font-for-all">{cardData.rating}</p>
                      </div>
                      <p className="font-for-all">Status:<a className="font-for-all" href="#0">{cardData.read_status}</a></p>
                  </div>
              </div>
          </div>
      )
  }

export default BookCard;
