import './BookDetails.css';
import Header from "../Header/Header";
import Footer from '../Footer/Footer';
// import { useNavigate } from "react-router-dom";
// import cookies from 'js-cookie';


const Bookshelves = () => {
//   const navigate = useNavigate();

    return(
        <div className="backgrounddiv">
            <Header/>
            <div className="card-div-background">
                <div className="card-div">
                    <img className="card-image" src="https://assets.ccbp.in/frontend/react-js/eyes-to-the-wind-book.png" alt="No_image"/>
                    <div className="books-details-div">
                        <h6 className="font-for-all heading">Title</h6>
                        <p className="font-for-all descrition">author_name</p>
                        <div className="rating-div">
                            <p className="font-for-all">Avg Rating: </p>
                            <p className="font-for-all">{cardData.rating}</p>
                        </div>
                        <p className="font-for-all">Status:<a className="font-for-all" href="#0">{cardData.read_status}</a></p>
                    </div>
                </div>
            </div>
            <Footer/>
        </div>
    )
        
}

export default Bookshelves;
