import React from "react";
// import {Link} from "react-router-dom";
import { useState } from "react";
import "./Login.css";


const Login=()=>{
  const initialValues={username:"",password:""}
  const [loginValues,setLoginValues]=useState(initialValues);
  const [loginErrors,setLoginErrors]=useState({});

  const handleChange=(e)=>{
    const {name,value}=e.target;
    setLoginValues({...loginValues,[name]:value});
    console.log(loginValues);
  }

  const handleSubmit=(e)=>{
    e.preventDefault();
    setLoginErrors(validations(loginValues));
  }

  const validations=(values)=>{
    const errors={};
    const regex=/^[a-zA-Z0-9]+$/;
    if(!values.username){
      errors.username="username is required!";
    }
  };

  return (
    <div className="background-main">
        <div className="mainn-container">
          <div className="second-main">
            <div className="imagediv">
              <img className="image" src="/images/Bookslogo1.jpeg" alt="login"/>
            </div>
            <div className="secondHalf">
              <div className="corner-btn">
                {/* <Link to="/Login">
                  <button className="btn-login" type="submit">Login</button>
                </Link> */}
                <button className="btn-login" type="submit">Login</button>
                {/* <Link to="/register">
                  <button className="btn-register" type="submit">Register</button>
                </Link> */}
                <button className="btn-register" type="submit">Register</button>
              </div>
              <div className="loginformdiv">
                <form onSubmit={handleSubmit} className="formitems">
                  <h1 className="main-heading">Login</h1>
                  <label className="h6 input-label">Username</label>
                  <input onChange={handleChange} className="input" type="text" placeholder="Enter your name" value={loginValues.username}/>
                  <label className="h6 input-label">Password</label>
                  <input onChange={handleChange} className="input" type="password" placeholder="Enter your password" value={loginValues.password}/>
                  {/* <div>
                    <button className="submit-btn" type="submit">Sign In</button>
                  </div> */}
                  <button className="submit-btn" type="submit">Sign In</button>
                  <div className="ancor">
                    <a className="anchor-link" href="#0">Forgot password?</a>
                    <a className="anchor-link" href="#0">Not a Member Yet?</a>
                  </div>
                </form>
              </div>
            </div>
          </div>
        </div>
    </div>
  );
};

export default Login;
